import React from "react";

import Card from "./Card";

const Cards = (props) => {

    let allcourses = [];

    const getCourses = () => {
        Object.values(courses).forEach( (category) => {
            category.forEach((course) => {
                allcourses.push(course);
            })
        })
        return allcourses;
    }


    return(
        <div>
            {
                getCourses().map((course) =>{
                    return(
                        <Card course ={course}></Card> 
                    )
                })
            }
        </div>
    )
}

export default Cards;
